free=audioread("free.wav");shave=audioread("shave.wav");Length=5000;free_part=free(1:Length,:);free_part=free_part';shave_part=shave(1:Length,:);shave_part=shave_part';FREE=abs(fft(free_part));SHAVE=abs(fft(shave_part));fs=44100;F=(0:Length-1)/Length*fs;t=(0:1/fs:(Length-1)/fs);subplot(2,2,1)plot(t,shave_part)subplot(2,2,2)plot(F,SHAVE)axis([0 2000 0 150])subplot(2,2,3)plot(t,free_part)subplot(2,2,4)plot(F,FREE)axis([0 2000 0 150])print -dpng "fig.png"