L=1001;t=[0:1/(L-1):1];f1=50;f2=60;f3=100;f4=70;noise=randn(1,L);wave1=sin(2*pi*f1*t)+cos(2*pi*f2*t);wave2=sin(2*pi*f3*t)+cos(2*pi*f4*t);signal1=wave1.+noise;SIG1=abs(fft(signal1));signal2=wave2.+noise;SIG2=abs(fft(signal2));F=(0:length(SIG1)-1)/length(SIG1)*L;subplot(2,2,1)plot(t,signal1)axis([0 1 -5 5])subplot(2,2,2)plot(F,SIG1)axis([0 200 0 600])subplot(2,2,3)plot(t,signal2)axis([0 1 -5 5])subplot(2,2,4)plot(F,SIG2)axis([0 200 0 600])