L=1001;t1=[0:1/(L-1):1];t2=[0:1/(L-1):3];noise1=randn(1,L);noise2=0.1*randn(1,L-1);wave=sin(2*pi*50*t)+cos(2*pi*60*t);new=wave.+noise1;NEW=abs(fft(new));F1=(0:length(NEW)-1)/length(NEW)*L;signal=horzcat(noise2,new,noise2);SI=abs(fft(signal));F2=(0:length(SI)-1)/length(SI)*L;subplot(2,2,1)plot(t1,new)axis([0 1 -5 5])subplot(2,2,2)plot(F1,NEW)axis([0 (L-1)/2 0 600])subplot(2,2,3)plot(t2,signal)subplot(2,2,4)plot(F2,SI)axis([0 500 0 600])